import {React, useState} from "react";
import AnimeImg from '../images/anime_collection.png';
import Card from 'react-bootstrap';
class Pokemon extends React.Component {
    // Constructor 
    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
    }

    // ComponentDidMount is used to
    // execute the code 
    componentDidMount() {
        var requestOptions = {
            method: 'GET',
            redirect: 'follow'
          };

          fetch("https://pokeapi.co/api/v2/pokemon/?limit=151", requestOptions)
          .then(response => response.text())
          .then((result) => {
            this.setState({
                items: result,
                DataisLoaded: true

            });
        })
}
    render() {
        const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h3> Loading.... </h3> </div> ;

        return (

        <div class="row">
              {
                items.map((item) => ( 

                   <h3 style={styles.h3}>{ item.name }</h3>
                ))
            }

        </div>
                );
                }
}

export default Pokemon;

const styles = {
  container: {
    alignContent: 'center',
    padding: '10rem'
  },
  img: {
    height: '30rem',
    width: '35rem',
    alignContent: 'center',
    marginRight: '1rem'

  },
  myArticle: {
    display: 'flex',
    flexDirection: 'column'
  },
  h3: {
    textAlign: 'center',
    marinTop: '1rem'
  }
}
