import React from 'react'
class Pokemon extends React.Component{
  constructor(props) {
    super(props);

    this.state = {
        items: [],
        DataisLoaded: false
    };
}
    // ComponentDidMount is used to
    // execute the code 
    componentDidMount() {
      const options = {
        method: 'GET',
        headers: {
          'X-RapidAPI-Key': 'b32397f1fcmsh935c3b21d22db73p1ab1b6jsn93997ae9ae46',
          'X-RapidAPI-Host': 'pokemon-tcg-card-prices.p.rapidapi.com'
        }
      };
      fetch(
        'https://pokemon-tcg-card-prices.p.rapidapi.com/card?setId=33ee55f4-30d0-4900-850f-36a351fb9719&set=vivid-voltage&series=sword-and-shield', options)
          .then((res) => res.json())
          .then((json) => {
              this.setState({
                  items: json,
                  DataisLoaded: true

              });
          })
  }
  render() {
      const { DataisLoaded, items } = this.state;
      if (!DataisLoaded) return <div>
          <h3> Loading.... </h3> </div> ;
  return(
    <div>
      <p>Pokemon Cards</p>
      {
                items.map((item) => ( 
                  <div class="col-lg-6">
                    <div class="card">
                    <div class="card-body">
                    <div class="card bg-dark text-white">

                <article style={styles.container} key = { item.cardId } >
                  {/* <img class="card-img" src= {item.animeImg} alt="Anime" style={styles.img}/>  */}
                  <div className="card-img-overlay"> 
                   <h3 style={styles.h3}>Anime: { item.name }</h3>
                    <div className="d-grid gap-2 d-sm-flex justify-content-md-center">
                    <button style={styles.btn} id="button" class="btn btn-primary btn-lg px-4 gap-3">View More</button>
                    <button id="button" class="btn btn-primary btn-lg px-4 gap-3">Follow</button>
                    </div>
                    </div>
                    </article>
                    </div>
                    </div>
                    </div>
                    </div>
                ))
            }

    </div>
    )
  }
}

export default Pokemon;

const styles = {
  container: {
    alignContent: 'center',
    padding: '10rem'
  },
  img: {
    height: '30rem',
    width: '35rem',
    alignContent: 'center',
    marginRight: '1rem'

  },
  myArticle: {
    display: 'flex',
    flexDirection: 'column'
  },
  h3: {
    textAlign: 'center',
    marinTop: '1rem'
  }
}
