import React, {useState} from 'react';
import {FaSearch} from 'react-icons/fa';
import {AiOutlineClose} from "react-icons/ai";

function SearchBar({placeholder, data}) {
    const [filteredData, setFilteredData] = useState([]);
    const [wordEntered, setWordEntered] = useState([]);
    const handleFilter = (event) => {
      const searchWord = event.target.value
      setWordEntered(searchWord);
      const newFilter = data.filter((value) => {
        return value.text.toLowerCase().includes(searchWord.toLowerCase());
      });
      if(searchWord === ""){
        setFilteredData([]);
      }else{
        setFilteredData(newFilter);
      }
    }
    const clearInput = () => {
      setFilteredData([]);
      setWordEntered("");
    }
return (
    <div className="search">
    <div className="searchInputs">
      <input type="text" placeholder={placeholder} value={wordEntered} onChange={handleFilter}/>
      <div className="searchIcon"><FaSearch/></div>
      {filteredData.length === 0 ? <FaSearch/> : <AiOutlineClose id="clearBtn" onClick={clearInput}/>}
    </div>
    {filteredData.length !==0 && (
    <div className="dataResult">{filteredData.map((value) => {
      return (
        <a className="dataItem" href={value.text}><p>{value.text}</p></a>
      );
    })}
    </div>
    )}
    </div>
)

}

export default SearchBar;
