import React, {useState} from 'react';
import {FaSearch} from 'react-icons/fa';
import {IoCloseSharp} from 'react-icons/io';

function SearchBar({placeholder, data}) {
    const [filteredData, setFilteredData] = useState([]);
    const handleFilter = (event) => {
      const searchWord = event.target.value
      const newFilter = data.filter((value) => {
        return value.text.toLowerCase().includes(searchWord.toLowerCase());
      });
      if(searchWord === ""){
        setFilteredData([]);
      }else{
        setFilteredData(newFilter);
      }
    }
    const clearInput = () => {
      setFilteredData([]);
    }
return (
    <div className="search">
    <div className="searchInputs">
      <input type="text" placeholder={placeholder} onChange={handleFilter}/>
      <div className="searchIcon"><FaSearch/></div>
      {filteredData.length === 0 ? <FaSearch/> : <IoCloseSharp id="clearBtn"/>}
    </div>
    {filteredData.length !=0 && (
    <div className="dataResult">{filteredData.map((value) => {
      return (
        <a className="dataItem"><p>{value.text}</p></a>
      );
    })}
    </div>
    )}
    </div>
)

}

export default SearchBar;
