import React, {useState} from 'react';
import ImageURL from '../images/avatar.ico';
import Image from '../images/mc_small_logo.png';
import Icon from './Icon';
import MyAvatar from './MyAvatar';
import {FaSearch} from 'react-icons/fa';
import Form from 'react-bootstrap/Form';
import { useNavigate} from 'react-router-dom';

const Header = ({data}) =>{
  const navigate = useNavigate();
  const navigateToSearchResult = () => {
  navigate('search');

  //filtering data
  const [filteredData, setFilteredData]= useState([]); 
  const handleFilter = (e) => {
    const searchWord = e.target.value
    const newFilter = data.filter((value) => {
      return value.title.toLowerCase().includes(searchWord);
    });
    setFilteredData(newFilter);
  };
    return (
        <header style={styles.myHeader}>
          <div style={styles.left}>
            <Icon MyIcon={Image}/> 
          </div>
            <Form>
            <div style={styles.searchCont}>
            <span style={styles.inputIcon}><FaSearch/></span>
            <input
            type="text"
            style={styles.inputwithIcon}
            placeholder="search collections" 
            onChange={handleFilter}/>


            <button onClick={navigateToSearchResult} id='button' variant='primary' type='submit'>Search</button>
            <div>
        </div>
            </div>
            <div className="dataResult">
              {filteredData.map((value, key) => {
                return (
                  <a className="dataItem" href={value.imageLink} target="_blank">
                    <p>{value.title}</p>
                  </a>
                );
              })}
            </div>
            </Form>
          <div style={styles.rightSide}>
            <MyAvatar AvatarIcon={ImageURL}/>
          </div>
        </header>

    );
}
}
export default Header

const styles = {
  myHeader: {
    padding:'1%',
    color: 'rgb(163,173,194)',
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    boxShadow: '1px 6px 1px 1px rgba(0, 0, 25, .1)'

  },
  left: {
    display: 'flex',
    flexDirection: 'row'
  },
  logo: {
    borderRadius: '50%',
    height: '50px',
    width: '60px',
    marginRight: '10%',
    backgroundColor: 'white',
    alignItems: 'center'

  },
  searchCont: {
    border: '1px solid #ddd',
    display: 'flex',
    flexDirection: 'row',
    borderRadius: '2px',
    width: '50rem',
    marginLeft: '1rem',
    height: '3.5rem',
  },
  inputIcon: {
    background: '#ddd',
    padding: '10px',
    display: 'flex',
    alignItems: 'center',
    fontSize: '1rem',
    color: '#650038'
  },
  inputwithIcon: {
    border: 'none',
    flex: 1,
    padding: '10px'
  },
  rightSide: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    marginRight: '3%'

  },
  rightIcons: {

  }
}
