import {React, useState} from 'react';
import Data from './ListData.json';

function SearchBar() {
    const [query, setQuery] = useState("")
return (
    <div>
        <div>
      <input placeholder="Search Collections" onChange={event => setQuery(event.target.value)}/>
    </div>
    {
  Data.filter(post => {
    if (query === '') {
      return post;
    } else if (post.title.toLowerCase().includes(query.toLowerCase())) {
      return post;
    }
  }).map((post, index) => (
    <div className="box" key={index}>
      <p>{post.title}</p>
      <p>{post.author}</p>
    </div>
  ))
}
    </div>
)


};

export default SearchBar;
